var textarea = document.getElementById('myTextArea');

textarea.addEventListener('input', function() {
  if (this.value.length > 500) {
    this.value = this.value.slice(0, 500);
  }
  this.style.height = 'auto';     // Réinitialise la hauteur 
  this.style.height = this.scrollHeight + 'px';  // Définit la hauteur en fonction du contenu
}, false);

document.querySelector("#phone-form").addEventListener("keypress", function (evt) {
  if (evt.which != 8 && evt.which != 0 && (evt.which < 48 || evt.which > 57 && evt.which != 43) || this.value.length >= 12)
  {
      evt.preventDefault();
  }
});